import data as datafrom django.shortcuts import render, redirectfrom django.http import HttpResponsefrom .models import Ligas, Equipo, Jugadores, Enfrentamientofrom .forms import LigasForm, EquipoForm, JugadoresForm, EnfrentamientoFormdef logout_view(request):    return redirect(request, "Equipo/index.html")# Create your views here.def Inicio(request):    return render(request, "inicio.html")def Registrate(request):    mensaje = ""    if request.method == 'POST':        if 'ligas' in request.POST:            formulario = LigasForm(request.POST)            if formulario.is_valid():                formulario.save()                mensaje = "Liga guardada correctamente"    if 'equipo' in request.POST:        formulario_equipo = EquipoForm(request.POST)        if formulario_equipo.is_valid():            formulario_equipo.save()            mensaje = "Equipo guardado correctamente"    if 'jugadores' in request.POST:        formulario_jugadores = JugadoresForm(request.POST)        if formulario_jugadores.is_valid():            formulario_jugadores.save()            mensaje = "Jugador guardado correctamente"    form_ligas = LigasForm()    form_equipo = EquipoForm()    formulario_jugadores = JugadoresForm()    return render(request, 'Equipo/index.html',                  {'form_ligas': form_ligas, 'form_equipo': form_equipo, 'form_jugadores': formulario_jugadores,                   'mensaje': mensaje})def Informacion(request):    ligas = Ligas.objects.all()    equipos = Equipo.objects.all()    jugadores = Jugadores.objects.all()    return render(request, 'Equipo/form.html', {'ligas': ligas, 'equipos': equipos, 'jugadores': jugadores})def Fechas(request):    mensaje = ""    Enfrentamiento_list = Enfrentamiento.objects.all()    if request.method == 'POST':        form = EnfrentamientoForm(request.POST)        if form.is_valid():            form.save()            mensaje = "Enfrentamiento guardado correctamente"            form = EnfrentamientoForm()    else:        form = EnfrentamientoForm()    return render(request, 'Enfrentamiento/enfrenta.html', {'form': form, 'mensaje': mensaje, 'Enfrentamiento': Enfrentamiento_list})def Contacto(request):    return render(request, "base.html")def Login(request):    return render(request, "login.html ")def crear(request):    return render(request, "Equipo/crear.html")def editar(request):    return render(request, "Equipo/editar.html")